          </node>
          <node TEXT="达伦贝尔判别法（比值）（自比）" ID="n8Qy4Kbruz" _mubu_text="%3Cspan%3E%E8%BE%BE%E4%BC%A6%E8%B4%9D%E5%B0%94%E5%88%A4%E5%88%AB%E6%B3%95%EF%BC%88%E6%AF%94%E5%80%BC%EF%BC%89%EF%BC%88%E8%87%AA%E6%AF%94%EF%BC%89%3C/span%3E" STYLE="fork">
            <node TEXT="对于正项级数，计算相邻两项的比值的极限值，如果极限值小于1，则级数收敛；如果极限值大于1或者无极限，则级数发散。" ID="gUMbzGhZub" _mubu_text="%3Cspan%3E%E5%AF%B9%E4%BA%8E%E6%AD%A3%E9%A1%B9%E7%BA%A7%E6%95%B0%EF%BC%8C%E8%AE%A1%E7%AE%97%E7%9B%B8%E9%82%BB%E4%B8%A4%E9%A1%B9%E7%9A%84%E6%AF%94%E5%80%BC%E7%9A%84%E6%9E%81%E9%99%90%E5%80%BC%EF%BC%8C%E5%A6%82%E6%9E%9C%E6%9E%81%E9%99%90%E5%80%BC%E5%B0%8F%E4%BA%8E1%EF%BC%8C%E5%88%99%E7%BA%A7%E6%95%B0%E6%94%B6%E6%95%9B%EF%BC%9B%E5%A6%82%E6%9E%9C%E6%9E%81%E9%99%90%E5%80%BC%E5%A4%A7%E4%BA%8E1%E6%88%96%E8%80%85%E6%97%A0%E6%9E%81%E9%99%90%EF%BC%8C%E5%88%99%E7%BA%A7%E6%95%B0%E5%8F%91%E6%95%A3%E3%80%82%3C/span%3E" STYLE="fork">
              <node TEXT="如果极限值小于1，意味着比值序列趋近于0。这表示随着n的增加，相邻两项的比值变得越来越小。在级数求和的过程中，后面的项对总和的贡献越来越小，因此级数的总和也趋近于一个有限值，即收敛。" ID="zEz5bKdCXP" _mubu_text="%3Cspan%3E%E5%A6%82%E6%9E%9C%E6%9E%81%E9%99%90%E5%80%BC%E5%B0%8F%E4%BA%8E1%EF%BC%8C%E6%84%8F%E5%91%B3%E7%9D%80%E6%AF%94%E5%80%BC%E5%BA%8F%E5%88%97%E8%B6%8B%E8%BF%91%E4%BA%8E0%E3%80%82%E8%BF%99%E8%A1%A8%E7%A4%BA%E9%9A%8F%E7%9D%80n%E7%9A%84%E5%A2%9E%E5%8A%A0%EF%BC%8C%E7%9B%B8%E9%82%BB%E4%B8%A4%E9%A1%B9%E7%9A%84%E6%AF%94%E5%80%BC%E5%8F%98%E5%BE%97%E8%B6%8A%E6%9D%A5%E8%B6%8A%E5%B0%8F%E3%80%82%E5%9C%A8%E7%BA%A7%E6%95%B0%E6%B1%82%E5%92%8C%E7%9A%84%E8%BF%87%E7%A8%8B%E4%B8%AD%EF%BC%8C%E5%90%8E%E9%9D%A2%E7%9A%84%E9%A1%B9%E5%AF%B9%E6%80%BB%E5%92%8C%E7%9A%84%E8%B4%A1%E7%8C%AE%E8%B6%8A%E6%9D%A5%E8%B6%8A%E5%B0%8F%EF%BC%8C%E5%9B%A0%E6%AD%A4%E7%BA%A7%E6%95%B0%E7%9A%84%E6%80%BB%E5%92%8C%E4%B9%9F%E8%B6%8B%E8%BF%91%E4%BA%8E%E4%B8%80%E4%B8%AA%E6%9C%89%E9%99%90%E5%80%BC%EF%BC%8C%E5%8D%B3%E6%94%B6%E6%95%9B%E3%80%82%3C/span%3E" STYLE="fork"/>
              <node TEXT="反之，如果极限值大于1或不存在，意味着比值序列趋近于无穷大或者不存在极限。这表示随着n的增加，相邻两项的比值变得越来越大或者不趋近于任何特定的值。在级数求和的过程中，后面的项对总和的贡献变得越来越大，无法控制总和的增长，因此级数发散。" ID="nFaX8y8b2a" _mubu_text="%3Cspan%3E%E5%8F%8D%E4%B9%8B%EF%BC%8C%E5%A6%82%E6%9E%9C%E6%9E%81%E9%99%90%E5%80%BC%E5%A4%A7%E4%BA%8E1%E6%88%96%E4%B8%8D%E5%AD%98%E5%9C%A8%EF%BC%8C%E6%84%8F%E5%91%B3%E7%9D%80%E6%AF%94%E5%80%BC%E5%BA%8F%E5%88%97%E8%B6%8B%E8%BF%91%E4%BA%8E%E6%97%A0%E7%A9%B7%E5%A4%A7%E6%88%96%E8%80%85%E4%B8%8D%E5%AD%98%E5%9C%A8%E6%9E%81%E9%99%90%E3%80%82%E8%BF%99%E8%A1%A8%E7%A4%BA%E9%9A%8F%E7%9D%80n%E7%9A%84%E5%A2%9E%E5%8A%A0%EF%BC%8C%E7%9B%B8%E9%82%BB%E4%B8%A4%E9%A1%B9%E7%9A%84%E6%AF%94%E5%80%BC%E5%8F%98%E5%BE%97%E8%B6%8A%E6%9D%A5%E8%B6%8A%E5%A4%A7%E6%88%96%E8%80%85%E4%B8%8D%E8%B6%8B%E8%BF%91%E4%BA%8E%E4%BB%BB%E4%BD%95%E7%89%B9%E5%AE%9A%E7%9A%84%E5%80%BC%E3%80%82%E5%9C%A8%E7%BA%A7%E6%95%B0%E6%B1%82%E5%92%8C%E7%9A%84%E8%BF%87%E7%A8%8B%E4%B8%AD%EF%BC%8C%E5%90%8E%E9%9D%A2%E7%9A%84%E9%A1%B9%E5%AF%B9%E6%80%BB%E5%92%8C%E7%9A%84%E8%B4%A1%E7%8C%AE%E5%8F%98%E5%BE%97%E8%B6%8A%E6%9D%A5%E8%B6%8A%E5%A4%A7%EF%BC%8C%E6%97%A0%E6%B3%95%E6%8E%A7%E5%88%B6%E6%80%BB%E5%92%8C%E7%9A%84%E5%A2%9E%E9%95%BF%EF%BC%8C%E5%9B%A0%E6%AD%A4%E7%BA%A7%E6%95%B0%E5%8F%91%E6%95%A3%E3%80%82%3C/span%3E" STYLE="fork"/>
              <node TEXT="因为正项级数的收敛与其部分和序列的有界性以及项之间的趋势有关。如果项的比值序列趋近于0，则部分和序列有界，级数收敛。如果项的比值序列趋近于无穷大或不存在极限，部分和序列无界，级数发散。" ID="ppcHjDLau7" _mubu_text="%3Cspan%3E%E5%9B%A0%E4%B8%BA%E6%AD%A3%E9%A1%B9%E7%BA%A7%E6%95%B0%E7%9A%84%E6%94%B6%E6%95%9B%E4%B8%8E%3C/span%3E%3Cspan%20class=%22bold%22%3E%E5%85%B6%E9%83%A8%E5%88%86%E5%92%8C%E5%BA%8F%E5%88%97%E7%9A%84%E6%9C%89%E7%95%8C%E6%80%A7%3C/span%3E%3Cspan%3E%E4%BB%A5%E5%8F%8A%3C/span%3E%3Cspan%20class=%22bold%22%3E%E9%A1%B9%E4%B9%8B%E9%97%B4%E7%9A%84%E8%B6%8B%E5%8A%BF%3C/span%3E%3Cspan%3E%E6%9C%89%E5%85%B3%E3%80%82%E5%A6%82%E6%9E%9C%E9%A1%B9%E7%9A%84%E6%AF%94%E5%80%BC%E5%BA%8F%E5%88%97%E8%B6%8B%E8%BF%91%E4%BA%8E0%EF%BC%8C%E5%88%99%E9%83%A8%E5%88%86%E5%92%8C%E5%BA%8F%E5%88%97%E6%9C%89%E7%95%8C%EF%BC%8C%E7%BA%A7%E6%95%B0%E6%94%B6%E6%95%9B%E3%80%82%E5%A6%82%E6%9E%9C%E9%A1%B9%E7%9A%84%E6%AF%94%E5%80%BC%E5%BA%8F%E5%88%97%E8%B6%8B%E8%BF%91%E4%BA%8E%E6%97%A0%E7%A9%B7%E5%A4%A7%E6%88%96%E4%B8%8D%E5%AD%98%E5%9C%A8%E6%9E%81%E9%99%90%EF%BC%8C%E9%83%A8%E5%88%86%E5%92%8C%E5%BA%8F%E5%88%97%E6%97%A0%E7%95%8C%EF%BC%8C%E7%BA%A7%E6%95%B0%E5%8F%91%E6%95%A3%E3%80%82%3C/span%3E" STYLE="fork"/>
            </node>
            <node TEXT="通项数列单调递减则收敛，数列单调递增必发散；" ID="UnvM1lQLV3" _mubu_text="%3Cspan%20class=%22strikethrough%22%3E%E9%80%9A%E9%A1%B9%E6%95%B0%E5%88%97%E5%8D%95%E8%B0%83%E9%80%92%E5%87%8F%E5%88%99%E6%94%B6%E6%95%9B%EF%BC%8C%E6%95%B0%E5%88%97%E5%8D%95%E8%B0%83%E9%80%92%E5%A2%9E%E5%BF%85%E5%8F%91%E6%95%A3%EF%BC%9B%3C/span%3E" STYLE="fork"/>
            <node TEXT="然而，比值极限为1 此法失效（比如p级数）。" ID="wsNJoh1aSe" _mubu_text="%3Cspan%3E%E7%84%B6%E8%80%8C%EF%BC%8C%E6%AF%94%E5%80%BC%E6%9E%81%E9%99%90%E4%B8%BA%3C/span%3E%3Cspan%20class=%22%20formula%22%20data-raw=%221%22%20contenteditable=%22false%22%3E%E2%80%8B%3C/span%3E%3Cspan%3E%20%E6%AD%A4%E6%B3%95%E5%A4%B1%E6%95%88%EF%BC%88%E6%AF%94%E5%A6%82p%E7%BA%A7%E6%95%B0%EF%BC%89%E3%80%82%3C/span%3E" STYLE="fork"/>
          </node>
          <node TEXT="柯西判别法（根植）（自比）" ID="MqFKI2IAJT" _mubu_text="%3Cspan%3E%E6%9F%AF%E8%A5%BF%E5%88%A4%E5%88%AB%E6%B3%95%EF%BC%88%E6%A0%B9%E6%A4%8D%EF%BC%89%EF%BC%88%E8%87%AA%E6%AF%94%EF%BC%89%3C/span%3E" STYLE="fork">
            <node TEXT="对于正项级数，计算相邻两项的比值的极限值，如果极限值小于1，则级数收敛；如果极限值大于1或者无极限，则级数发散。" ID="r2Ju0thWg7" _mubu_text="%3Cspan%3E%E5%AF%B9%E4%BA%8E%E6%AD%A3%E9%A1%B9%E7%BA%A7%E6%95%B0%EF%BC%8C%E8%AE%A1%E7%AE%97%E7%9B%B8%E9%82%BB%E4%B8%A4%E9%A1%B9%E7%9A%84%E6%AF%94%E5%80%BC%E7%9A%84%E6%9E%81%E9%99%90%E5%80%BC%EF%BC%8C%E5%A6%82%E6%9E%9C%E6%9E%81%E9%99%90%E5%80%BC%E5%B0%8F%E4%BA%8E1%EF%BC%8C%E5%88%99%E7%BA%A7%E6%95%B0%E6%94%B6%E6%95%9B%EF%BC%9B%E5%A6%82%E6%9E%9C%E6%9E%81%E9%99%90%E5%80%BC%E5%A4%A7%E4%BA%8E1%E6%88%96%E8%80%85%E6%97%A0%E6%9E%81%E9%99%90%EF%BC%8C%E5%88%99%E7%BA%A7%E6%95%B0%E5%8F%91%E6%95%A3%E3%80%82%3C/span%3E" STYLE="fork">
              <node TEXT="如果极限值小于1，意味着根式序列趋近于0。这表示随着n的增加，项的根式趋于零，说明每一项趋于零的速度比n的增长速度更快。在级数求和的过程中，后面的项对总和的贡献越来越小，因此级数的总和也趋近于一个有限值，即收敛。" ID="vSNOnQHQRy" _mubu_text="%3Cspan%3E%E5%A6%82%E6%9E%9C%E6%9E%81%E9%99%90%E5%80%BC%E5%B0%8F%E4%BA%8E1%EF%BC%8C%E6%84%8F%E5%91%B3%E7%9D%80%E6%A0%B9%E5%BC%8F%E5%BA%8F%E5%88%97%E8%B6%8B%E8%BF%91%E4%BA%8E0%E3%80%82%E8%BF%99%E8%A1%A8%E7%A4%BA%E9%9A%8F%E7%9D%80n%E7%9A%84%E5%A2%9E%E5%8A%A0%EF%BC%8C%E9%A1%B9%E7%9A%84%E6%A0%B9%E5%BC%8F%E8%B6%8B%E4%BA%8E%E9%9B%B6%EF%BC%8C%E8%AF%B4%E6%98%8E%E6%AF%8F%E4%B8%80%E9%A1%B9%E8%B6%8B%E4%BA%8E%E9%9B%B6%E7%9A%84%E9%80%9F%E5%BA%A6%E6%AF%94n%E7%9A%84%E5%A2%9E%E9%95%BF%E9%80%9F%E5%BA%A6%E6%9B%B4%E5%BF%AB%E3%80%82%E5%9C%A8%E7%BA%A7%E6%95%B0%E6%B1%82%E5%92%8C%E7%9A%84%E8%BF%87%E7%A8%8B%E4%B8%AD%EF%BC%8C%E5%90%8E%E9%9D%A2%E7%9A%84%E9%A1%B9%E5%AF%B9%E6%80%BB%E5%92%8C%E7%9A%84%E8%B4%A1%E7%8C%AE%E8%B6%8A%E6%9D%A5%E8%B6%8A%E5%B0%8F%EF%BC%8C%E5%9B%A0%E6%AD%A4%E7%BA%A7%E6%95%B0%E7%9A%84%E6%80%BB%E5%92%8C%E4%B9%9F%E8%B6%8B%E8%BF%91%E4%BA%8E%E4%B8%80%E4%B8%AA%E6%9C%89%E9%99%90%E5%80%BC%EF%BC%8C%E5%8D%B3%E6%94%B6%E6%95%9B%E3%80%82%3C/span%3E" STYLE="fork"/>
              <node TEXT="反之，如果极限值大于1或不存在，意味着根式序列趋近于无穷大或者不存在极限。这表示随着n的增加，项的根式趋于无穷大或者没有确定的极限值。在级数求和的过程中，后面的项对总和的贡献变得越来越大，无法控制总和的增长，因此级数发散。" ID="Cun6bBU80t" _mubu_text="%3Cspan%3E%E5%8F%8D%E4%B9%8B%EF%BC%8C%E5%A6%82%E6%9E%9C%E6%9E%81%E9%99%90%E5%80%BC%E5%A4%A7%E4%BA%8E1%E6%88%96%E4%B8%8D%E5%AD%98%E5%9C%A8%EF%BC%8C%E6%84%8F%E5%91%B3%E7%9D%80%E6%A0%B9%E5%BC%8F%E5%BA%8F%E5%88%97%E8%B6%8B%E8%BF%91%E4%BA%8E%E6%97%A0%E7%A9%B7%E5%A4%A7%E6%88%96%E8%80%85%E4%B8%8D%E5%AD%98%E5%9C%A8%E6%9E%81%E9%99%90%E3%80%82%E8%BF%99%E8%A1%A8%E7%A4%BA%E9%9A%8F%E7%9D%80n%E7%9A%84%E5%A2%9E%E5%8A%A0%EF%BC%8C%E9%A1%B9%E7%9A%84%E6%A0%B9%E5%BC%8F%E8%B6%8B%E4%BA%8E%E6%97%A0%E7%A9%B7%E5%A4%A7%E6%88%96%E8%80%85%E6%B2%A1%E6%9C%89%E7%A1%AE%E5%AE%9A%E7%9A%84%E6%9E%81%E9%99%90%E5%80%BC%E3%80%82%E5%9C%A8%E7%BA%A7%E6%95%B0%E6%B1%82%E5%92%8C%E7%9A%84%E8%BF%87%E7%A8%8B%E4%B8%AD%EF%BC%8C%E5%90%8E%E9%9D%A2%E7%9A%84%E9%A1%B9%E5%AF%B9%E6%80%BB%E5%92%8C%E7%9A%84%E8%B4%A1%E7%8C%AE%E5%8F%98%E5%BE%97%E8%B6%8A%E6%9D%A5%E8%B6%8A%E5%A4%A7%EF%BC%8C%E6%97%A0%E6%B3%95%E6%8E%A7%E5%88%B6%E6%80%BB%E5%92%8C%E7%9A%84%E5%A2%9E%E9%95%BF%EF%BC%8C%E5%9B%A0%E6%AD%A4%E7%BA%A7%E6%95%B0%E5%8F%91%E6%95%A3%E3%80%82%3C/span%3E" STYLE="fork"/>
              <node TEXT="根值判别法也是基于正项级数的收敛与其部分和序列的有界性以及项之间的趋势有关。如果项的根式序列趋近于零，则部分和序列有界，级数收敛。如果项的根式序列趋近于无穷大或不存在极限，部分和序列无界，级数发散。" ID="NfGOAYgBP7" _mubu_text="%3Cspan%3E%E6%A0%B9%E5%80%BC%E5%88%A4%E5%88%AB%E6%B3%95%E4%B9%9F%E6%98%AF%E5%9F%BA%E4%BA%8E%3C/span%3E%3Cspan%20class=%22bold%22%3E%E6%AD%A3%E9%A1%B9%E7%BA%A7%E6%95%B0%E7%9A%84%E6%94%B6%E6%95%9B%E4%B8%8E%E5%85%B6%E9%83%A8%E5%88%86%E5%92%8C%E5%BA%8F%E5%88%97%E7%9A%84%E6%9C%89%E7%95%8C%E6%80%A7%E4%BB%A5%E5%8F%8A%E9%A1%B9%E4%B9%8B%E9%97%B4%E7%9A%84%E8%B6%8B%E5%8A%BF%E6%9C%89%E5%85%B3%3C/span%3E%3Cspan%3E%E3%80%82%E5%A6%82%E6%9E%9C%E9%A1%B9%E7%9A%84%E6%A0%B9%E5%BC%8F%E5%BA%8F%E5%88%97%E8%B6%8B%E8%BF%91%E4%BA%8E%E9%9B%B6%EF%BC%8C%E5%88%99%E9%83%A8%E5%88%86%E5%92%8C%E5%BA%8F%E5%88%97%E6%9C%89%E7%95%8C%EF%BC%8C%E7%BA%A7%E6%95%B0%E6%94%B6%E6%95%9B%E3%80%82%E5%A6%82%E6%9E%9C%E9%A1%B9%E7%9A%84%E6%A0%B9%E5%BC%8F%E5%BA%8F%E5%88%97%E8%B6%8B%E8%BF%91%E4%BA%8E%E6%97%A0%E7%A9%B7%E5%A4%A7%E6%88%96%E4%B8%8D%E5%AD%98%E5%9C%A8%E6%9E%81%E9%99%90%EF%BC%8C%E9%83%A8%E5%88%86%E5%92%8C%E5%BA%8F%E5%88%97%E6%97%A0%E7%95%8C%EF%BC%8C%E7%BA%A7%E6%95%B0%E5%8F%91%E6%95%A3%E3%80%82%3C/span%3E" STYLE="fork"/>
            </node>
            <node TEXT="通项数列单调递减则收敛，数列单调递增必发散；" ID="tieU9s9t1R" _mubu_text="%3Cspan%20class=%22strikethrough%22%3E%E9%80%9A%E9%A1%B9%E6%95%B0%E5%88%97%E5%8D%95%E8%B0%83%E9%80%92%E5%87%8F%E5%88%99%E6%94%B6%E6%95%9B%EF%BC%8C%E6%95%B0%E5%88%97%E5%8D%95%E8%B0%83%E9%80%92%E5%A2%9E%E5%BF%85%E5%8F%91%E6%95%A3%EF%BC%9B%3C/span%3E" STYLE="fork"/>
            <node TEXT="然而，根植极限为1 此法失效（比如p级数）。" ID="BTY4miHCNf" _mubu_text="%3Cspan%3E%E7%84%B6%E8%80%8C%EF%BC%8C%E6%A0%B9%E6%A4%8D%E6%9E%81%E9%99%90%E4%B8%BA%3C/span%3E%3Cspan%20class=%22%20formula%22%20data-raw=%221%22%20contenteditable=%22false%22%3E%E2%80%8B%3C/span%3E%3Cspan%3E%20%E6%AD%A4%E6%B3%95%E5%A4%B1%E6%95%88%EF%BC%88%E6%AF%94%E5%A6%82p%E7%BA%A7%E6%95%B0%EF%BC%89%E3%80%82%3C/span%3E" STYLE="fork"/>
