          <node TEXT="莱布尼茨判别法" ID="O6D9yFU965" _mubu_text="%3Cspan%3E%E8%8E%B1%E5%B8%83%E5%B0%BC%E8%8C%A8%E5%88%A4%E5%88%AB%E6%B3%95%3C/span%3E" STYLE="fork">
            <node TEXT="u_n单调不增（关键验证u_n &gt;= u_{n + 1} ,可用导数证明单调），且极限为0，则交错级数收敛；" ID="YGefMHE6Ye" _mubu_text="%3Cspan%20class=%22%20formula%22%20data-raw=%22u_n%22%20contenteditable=%22false%22%3E%E2%80%8B%E2%80%8B%E2%80%8B%3C/span%3E%3Cspan%3E%E5%8D%95%E8%B0%83%E4%B8%8D%E5%A2%9E%3C/span%3E%3Cspan%20class=%22bold%22%3E%EF%BC%88%E5%85%B3%E9%94%AE%E9%AA%8C%E8%AF%81%3C/span%3E%3Cspan%20class=%22bold%20formula%22%20data-raw=%22u_n%2520%253E%253D%2520u_%257Bn%2520%252B%25201%257D%2520%22%20contenteditable=%22false%22%3E%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%3C/span%3E%3Cspan%20class=%22bold%22%3E,%E5%8F%AF%E7%94%A8%E5%AF%BC%E6%95%B0%E8%AF%81%E6%98%8E%E5%8D%95%E8%B0%83%EF%BC%89%EF%BC%8C%3C/span%3E%3Cspan%3E%E4%B8%94%E6%9E%81%E9%99%90%E4%B8%BA0%EF%BC%8C%E5%88%99%E4%BA%A4%E9%94%99%E7%BA%A7%E6%95%B0%E6%94%B6%E6%95%9B%EF%BC%9B%3C/span%3E" STYLE="fork">
              <node TEXT="正项部分和序列S_2n是单调递增的，且有界，因为它是一个非负单调递增序列;" ID="UjaHeioyDG" _mubu_text="%3Cspan%3E%E6%AD%A3%E9%A1%B9%E9%83%A8%E5%88%86%E5%92%8C%E5%BA%8F%E5%88%97%3C/span%3E%3Cspan%20class=%22%20formula%22%20data-raw=%22S_2n%22%20contenteditable=%22false%22%3E%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%3C/span%3E%3Cspan%3E%E6%98%AF%E5%8D%95%E8%B0%83%E9%80%92%E5%A2%9E%E7%9A%84%EF%BC%8C%E4%B8%94%E6%9C%89%E7%95%8C%EF%BC%8C%E5%9B%A0%E4%B8%BA%E5%AE%83%E6%98%AF%E4%B8%80%E4%B8%AA%E9%9D%9E%E8%B4%9F%E5%8D%95%E8%B0%83%E9%80%92%E5%A2%9E%E5%BA%8F%E5%88%97;%3C/span%3E" STYLE="fork"/>
              <node TEXT="负项部分和序列S_{2n-1}是单调递减的，且有界，因为它是一个非正单调递减序列;" ID="3sVNhFy67V" _mubu_text="%3Cspan%3E%E8%B4%9F%E9%A1%B9%E9%83%A8%E5%88%86%E5%92%8C%E5%BA%8F%E5%88%97%3C/span%3E%3Cspan%20class=%22%20formula%22%20data-raw=%22S_%257B2n-1%257D%22%20contenteditable=%22false%22%3E%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%E2%80%8B%3C/span%3E%3Cspan%3E%E6%98%AF%E5%8D%95%E8%B0%83%E9%80%92%E5%87%8F%E7%9A%84%EF%BC%8C%E4%B8%94%E6%9C%89%E7%95%8C%EF%BC%8C%E5%9B%A0%E4%B8%BA%E5%AE%83%E6%98%AF%E4%B8%80%E4%B8%AA%E9%9D%9E%E6%AD%A3%E5%8D%95%E8%B0%83%E9%80%92%E5%87%8F%E5%BA%8F%E5%88%97;%3C/span%3E" STYLE="fork"/>
              <node TEXT="根据单调有界定理，正项部分和序列S2n和负项部分和序列S2n-1都是收敛的。" ID="sM6S7MjBVJ" _mubu_text="%3Cspan%3E%E6%A0%B9%E6%8D%AE%E5%8D%95%E8%B0%83%E6%9C%89%E7%95%8C%E5%AE%9A%E7%90%86%EF%BC%8C%E6%AD%A3%E9%A1%B9%E9%83%A8%E5%88%86%E5%92%8C%E5%BA%8F%E5%88%97S2n%E5%92%8C%E8%B4%9F%E9%A1%B9%E9%83%A8%E5%88%86%E5%92%8C%E5%BA%8F%E5%88%97S2n-1%E9%83%BD%E6%98%AF%E6%94%B6%E6%95%9B%E7%9A%84%E3%80%82%3C/span%3E" STYLE="fork"/>
